#coding:utf-8from selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.common.keys import Keysfrom selenium.webdriver.support.ui import Selectfrom selenium.common.exceptions import NoSuchElementExceptionfrom selenium.webdriver.common.action_chains import ActionChainsfrom selenium.webdriver.common.alert import Alertimport unittest,time,reclass Baidu(unittest.TestCase):    def setUp(self):        self.browser = webdriver.Chrome()        self.browser.maximize_window()        self.browser.implicitly_wait(30)        self.base_url = 'https://www.baidu.com/?tn=99331982_hao_pg'        self.verificationErrors = []        self.accept_next_alert = True    #百度搜索的用例    def testbaidu_serach(self):        '''百度搜索'''        browser = self.browser        browser.get(self.base_url)        browser.find_element_by_id('kw').send_keys('selenium webdriver')        browser.find_element_by_id('su').click()        time.sleep(2)        #browser.close()    #百度设置的用例    def testbaidu_set(self):        '''百度设置'''        browser = self.browser        browser.get(self.base_url)        target1 = browser.find_element_by_xpath('//*[@id="u1"]/a[8]').click() #找到设置按钮        #ActionChains(browser).move_to_element(target1).perform()        browser.find_element_by_link_text('搜索设置').click() #点击搜索设置        print('到这')        serch = browser.current_window_handle        s = Select(browser.find_element_by_xpath('//*[@id="nr"]'))        s.select_by_value('50')        browser.find_element_by_xpath('//*[@id="gxszButton"]/a[1]').click()        time.sleep(3)        alert = browser.switch_to.alert   #千万记住这里alert后没有（）        print(alert.text)        alert.accept()        self.browser.quit()        self.assertEqual([],self.verificationErrors)    def tearDown(self):        self.browser.close()        self.assertEqual([],self.verificationErrors)#测试if __name__ == '__main__':    unittest.main()