#coding:utf-8import os,timefrom datetime import datetimeimport smtplibfrom email.mime.text import MIMETextfrom email.header import Headerreportdir = 'C://app//pycharm//REPORT'files = os.listdir(reportdir)print(files)'''L=[]for x in files:    a=(x.split(' '))[1]    L.append(a)print('L:',L)'''new_times=[]   #创建空列表for i in files:    b=os.path.getmtime(reportdir+'//'+i)  #单个文件的时间    new_times.append(b)   #将每个时间加入列表print(new_times)          #先打印出所有文件的时间for x in files:    if os.path.getmtime(reportdir+'//'+x)==max(new_times):   #如果这个文件的时间是时间列表中最大的，那么打印出这个文件        print('最新的文件为:%s' % x)lastdir = reportdir+'//'+xprint('最新的报告的路劲是：%s' % lastdir)with open(lastdir,'rb') as f:    R=f.read()print(R)#这一块的代码其实主要就是找出一个目录下最新的文件#下面开始整合邮件怎样将找到的最新的报告发送出去