#coding:utf-8import smtplibfrom email.mime.text import MIMETextfrom email.header import Header#发送邮箱sender = '15651797525@163.com'#接收邮箱receiver = '970185127@qq.com'receiver2 = '1584570900@qq.com'#发送邮箱的用户名密码username = '15651797525@163.com'password = 'xgh3213242016'#邮件主题subject = '关于我的测试报告'#发送邮箱的服务器smtpsever = 'smtp.163.com'  #163邮箱的服务器#中文需参数‘utf-8’ 单字节字符不需要msg = MIMEText('这是一封邮件，我想告诉你，我爱你','plain','utf-8')msg['Subject'] = Header(subject,'utf-8')msg['From'] = '15651797525@163.com'msg['To'] = '970185127@qq.com'#上面这一部分好像必须是这个格式，不然会报错吧smtp = smtplib.SMTP(smtpsever,25)#实例化smtp.connect('smtp.163.com') #链接上服务器smtp.login(username,password)smtp.sendmail(sender,[receiver,receiver2],msg.as_string())smtp.quit()